<?php
 function render_block_core_site_tagline( $attributes ) { $site_tagline = get_bloginfo( 'description' ); if ( ! $site_tagline ) { return; } $tag_name = 'p'; $align_class_name = empty( $attributes['textAlign'] ) ? '' : "has-text-align-{$attributes['textAlign']}"; $wrapper_attributes = get_block_wrapper_attributes( array( 'class' => $align_class_name ) ); if ( isset( $attributes['level'] ) && 0 !== $attributes['level'] ) { $tag_name = 'h' . (int) $attributes['level']; } return sprintf( '<%1$s %2$s>%3$s</%1$s>', $tag_name, $wrapper_attributes, $site_tagline ); } function register_block_core_site_tagline() { register_block_type_from_metadata( __DIR__ . '/site-tagline', array( 'render_callback' => 'render_block_core_site_tagline', ) ); } add_action( 'init', 'register_block_core_site_tagline' ); 